import React, { Component } from 'react'



export default class Button1 extends Component {
  constructor() {
    super()
    this.handleClick = this.handleClick.bind(this)
    this.state = {
      numeroDeCliques: 0,
    }
  }

  handleClick() {
    this.setState((estadoAnterior, _props) => ({
      numeroDeCliques: estadoAnterior.numeroDeCliques + 1
    }))
    console.log('Clicou no botão que salva!')
  }

  render() {
    return (
      <button onClick={this.handleClick} style={(this.state.numeroDeCliques % 2 === 0) ? 'green' : 'red'}>{`Salvar : ${this.state.numeroDeCliques}`}</button> 
    )
  }
}
