import React, { Component } from 'react'

export default class Form extends Component {
  constructor() {
    super()

    this.state = {
      nome: '',
      email: '',
    }

    this.handleNameChange = this.handleNameChange.bind(this)
  }

  handleNameChange (evento) {
    const { target } = evento
    this.setState({
      nome: target.value.toUpperCase
    })
    console.log(target.value)
  }


  render() {
    return (
      <form>
        <fieldset>
          <legend>Formulário Bloco 12</legend>
          <label>Name: 
            <input type="text" name="nome" maxLength = '40' placeholder="Digite seu nome aqui" onChange={this.handleNameChange} required/>
          </label>
          <label>Email: 
            <input type="text" name="email" placeholder="Digite seu email aqui"/>
          </label>
        </fieldset>
      </form>
    )
  }
}
