import React, { Component } from 'react'
import MyInput from './MyInput'
export default class Form extends Component {
  constructor() {
    super()

    this.state = {
      nome: '',
      email: '',
      cpf: '',
      endereco: '',
      cidade: '',
      estado: '',
      tipo: '',
    }

    this.handleChanges = this.handleChanges.bind(this) 

  }

  handleChanges (evento) {
    const { target } = evento
    const { name } = target
    this.setState({
      [ name ]: target.value
    })
  }


  render() {
    return (
      <form>
        <fieldset>
          <legend>Formulário Bloco 12</legend>
          <label>Name: 
            <input type="text" name="nome" maxlength = '40' placeholder="Digite seu nome aqui"  onC = {this.handleChanges.toUpperCase()}required/>
          </label>
          <label>Email: 
            <input type="text" name="email" placeholder="Digite seu email aqui"/>
          </label>
          <MyInput handleChanges={this.handleChanges} type="text" name="cpf" maxlength = '11' placeholder="Digite seu email aqui" required />
        </fieldset>
      </form>
    )
  }
}
